def decrypt(c):
    p = []
    for i, ch in enumerate(c):
        if ch.isalpha():
            chi = ord(ch) - 65
            p.append(chr(((chi - i) % 26) + 65))
        else:
            p.append(ch)
    return ''.join(p)

